import React, {useEffect, useState} from 'react';
import './App.css';

const Button = styled.button`
  background-color: black;
  color: white;
  font-size: 20px;
  padding: 10px 60px;
  border-radius: 5px;
  margin: 10px 0px;
  cursor: pointer;
`;



function App(){
  // init variable for data from backend
  const [backendData, setBackendData] = useState([{}])

  // retreiving data from backend api

  useEffect(() => {
    fetch("/api").then(
      response => response.json()
    ).then(
      // setting const data variable from retrived data
      data => {
        setBackendData(data)
      }
    )
  }, [])

  return(
    <div className = "App">
      <div data-reactroot>
        <h2> Enter a number below to find all prime numbers it is greater than</h2>
        <h2>
          <form>
            <label>
              <input type ="text" name ="number" />
            </label>
            <Button onClick={sayHello}>
              Disabled Button
            </Button>
          </form>
        </h2>
        {(typeof backendData.users === 'undefined') ? (
          <p> Loading....</p>
        ): (
          backendData.users.map((user, i)=> (
            <p key ={i}>{user}</p>
          ))
        )}
      </div>
    </div>

  )
}

export default App
