import React, {useEffect, useState} from 'react';
import './App.css';
//import axios from "axios";

function App(){
  // creating hook
  const [isSubmitted, setSubmit] = useState(false)
  const [Data, setData] = useState()
  const [Result, setResult] = useState(0)

  const handleChange = (e) =>{
    e.preventDefault()
    var number = parseInt(e.target.value)
    setData(number)
    //console.log(Data)
  }

  const handleSubmit = async (e) =>{
    e.preventDefault()
    if(Data<3){
      alert('There are no prime numbers less than 2, please enter a number above 2', Data)
      setSubmit(false)
      return 
    }

    setSubmit(true)
    //axios ({method: 'get', url: '/api', data:number})
    fetch("/api", {
      method: "POST",
      headers: {"Content-Type": "application/json"},
      body: JSON.stringify({ "Number": Data })
    }).then((response) => response.json()).then((result) => {
      console.log(result)
      setResult(result)
    }) 
  }


  return(
    <div>
      <h1> Enter a Number</h1>
      <form  onSubmit= {handleSubmit}>
          <input type="number"  onChange = {handleChange}></input>
          <button type = "submit" > Go</button>
      </form>
      <div> 
        {(typeof Result.result !== 'undefined' && isSubmitted) && <h1> Median is </h1> &&
            Result.result.map ((result) =>
            <h2> Median: {result}</h2>
            )
        }
      </div>
    </div>

  )
// <div className = "form">
// <form onSubmit = {handleSubmit} ></form> 
}

export default App
